from collections import Counter
def isValid(s):
    dicts = Counter(s)
    freq = dicts.values()
    freqset = set(freq)
    if len(freqset) == 1:
        return "YES"
    elif len(freqset) > 2:
        return "NO"
    else:
        freqset = list(freqset)
        f1 = freqset[0]
        f2 = freqset[1]

        dict1 = {f1:0, f2:0}
        for i in freq:
            dict1[i] += 1
            if dict1[f1] > 1 and dict1[f2]> 1:
                return "NO"
        return "YES"

if __name__ == '__main__':
    s= ""
    # s = "abcdefghhgfedecba"
    print(isValid(s))